import "./notword_popup.css"



const Notword_popup = () => {


const [not_word, setnot_word] = useState(false);


 useEffect(() => {
setnot_word(true);
}, []);



useEffect(() => {
setTimeout(() => setnot_word(false),1000)
}, []);


//need to makae this scaleable. I.e it only works once.
